 Using default 
 " . format ( str ( font_searchpath ))) font = ImageFont . load_default () return font view_alignment ( aligned , fontsize = '9pt' , show_N = 100 , colorscheme =< function aa_chemistry_simple at 0x7f6b134488b0 > , boxwidth = 9 , boxheight = 15 , label_width = None , show_descriptions = False , show_grouping = False ) Bokeh sequence alignment view for protein and nucleic acid sequences :sa: https://dmnfarrell.github.io/bioinformatics/bokeh-sequence-aligner :param aligned: MultipleSeqAlignment object :param fontsize: font size for text labels :param show_N: size of sequence window (in number of sequence letters) :param colorscheme: a Callable that returns a weblogo ColorScheme object :param boxwidth: column width of alignment :param boxheight: row height of alignment :param label_width: maximum length of row label; if None, extend to maximum label length :param show_descriptions: if True, show SeqRecord description for each row :param show_grouping: if True, highlight changes from reference in red against green background, instead of using the residue colorscheme :returns: A Bokeh plot of the Multiple Sequence Alignment. Source code in seqlike/draw_utils.py def view_alignment ( aligned , fontsize = "9pt" , show_N = 100 , colorscheme : Callable = aa_chemistry_simple , boxwidth = 9 , boxheight = 15 , label_width = None , show_descriptions = False , show_grouping = False , ): """Bokeh sequence alignment view for protein and nucleic acid sequences :sa: https://dmnfarrell.github.io/bioinformatics/bokeh-sequence-aligner :param aligned: MultipleSeqAlignment object :param fontsize: font size for text labels :param show_N: size of sequence window (in number of sequence letters) :param colorscheme: a Callable that returns a weblogo ColorScheme object :param boxwidth: column width of alignment :param boxheight: row height of alignment :param label_width: maximum length of row label; if None, extend to maximum label length :param show_descriptions: if True, show SeqRecord description for each row :param show_grouping: if True, highlight changes from reference in red against green background, instead of using the residue colorscheme :returns: A Bokeh plot of the Multiple Sequence Alignment. """ from bokeh.models import ColumnDataSource , Range1d from bokeh.plotting import figure from bokeh.models.glyphs import Rect def get_colors ( seqs , color_scheme ): """make colors for letters in sequence :param seqs: A string sequence. :param color_scheme: A string. :returns: a sequence of colors for each letter in seqs. """ # get colors color_dict = convert_colorscheme_to_color_map ( color_scheme , color_format = "hex" ) # assign colors to sequences text = [ i for s in list ( seqs ) for i in s ] return [ color_dict [ a ] for a in text ] def get_colors_for_matching ( seqs ): """match/mismatch color scheme for show_grouping :param seqs: Sequences for which colors need to be matched. :returns: a list of colors (strings) """ refseq = seqs [ 0 ] colors = list () for seq in list ( seqs ): for xs , ref_s in zip ( seq , refseq ): colors . append ( apply_matching_colorscheme ( xs , ref_s , color_format = "hex" )) return colors # make sequence and id lists from the aligned object seqs = [ rec . seq for rec in ( aligned )] if show_descriptions : labels = [ f " { row } - { rec . description } ( { rec . id } )" for ( row , rec ) in enumerate ( aligned )] else : labels = [ f " { row } - { rec . id } " for ( row , rec ) in enumerate ( aligned )] if label_width : labels = [ label [: label_width ] for label in labels ] else : label_width = max ( len ( label ) for label in labels ) text = [ i for s in list ( seqs ) for i in s ] if show_grouping : colors = get_colors_for_matching ( seqs ) else : colors = get_colors ( seqs , colorscheme ) N = len ( seqs [ 0 ]) S = len ( seqs ) x = np . arange ( 1 , N + 1 ) # need to reverse y so that sequences are plotted top-to-bottom y = np . arange ( S - 1 , - 1 , - 1 ) # creates a 2D grid of coords from the 1D arrays xx , yy = np . meshgrid ( x , y ) # flattens the arrays gx = xx . ravel () gy = yy . flatten () # use recty for rect coords with an offset recty = gy + 0.5 # now we can create the ColumnDataSource with all the arrays source = ColumnDataSource ( dict ( x = gx , y = gy , recty = recty , text = text , colors = colors )) plot_height = len ( seqs ) * boxheight + 50 x_range = Range1d ( 0 , N + 1 , bounds = "auto" ) viewlen = min ( show_N , N ) # view_range is for the close up view view_range = ( 0 , viewlen ) tools = "xpan,xwheel_zoom,reset,save" # plot_width combines length of text labels and number of letters in sequence view window # note: this part requires additional tuning; 5 pixel average width of y-axis labels is a guess plot_width = int ( 5 * label_width ) + boxwidth * viewlen + 40 # entire sequence view (no text, with zoom) p = figure ( title = None , width = plot_width , height = 50 , x_range = x_range , y_range = ( 0 , S ), tools = tools , min_border = 0 , toolbar_location = "below" , ) rects = Rect ( x = "x" , y = "recty" , width = 1 , height = 1 , fill_color = "colors" , line_color = None , fill_alpha = 0.6 , ) p . add_glyph ( source , rects ) p . yaxis . visible = False p . grid . visible = False # sequence text view with ability to scroll along x axis p1 = figure ( title = None , width = plot_width , height = plot_height , x_range = view_range , y_range = labels [:: - 1 ], tools = "xpan,reset,save" , min_border = 0 , toolbar_location = "below" , ) # , lod_factor=1) glyph = bk . models . glyphs . Text ( x = "x" , y = "y" , text = "text" , text_align = "center" , text_color = "black" , text_font = bk . core . properties . value ( "monospace" ), text_font_size = fontsize , ) rects = Rect ( x = "x" , y = "recty" , width = 1 , height = 1 , fill_color = "colors" , line_color = None , fill_alpha = 0.4 , ) p1 . add_glyph ( source , glyph ) p1 . add_glyph ( source , rects ) p1 . grid . visible = False p1 . xaxis . major_label_text_font_style = "bold" p1 . yaxis . minor_tick_line_width = 0 p1 . yaxis . major_tick_line_width = 0 p = bk . layouts . gridplot ([[ p ], [ p1 ]], toolbar_location = "below" ) bk . plotting . show ( p ) return p

